// 處理碰撞
const handleCollisions = () => {
  // 檢查角色和其他物體之間的碰撞
  if (checkCollision(player, slime)) {
    // 如果碰到 slime，停止角色移動
    player.x -= dirX * speed;
    player.y -= dirY * speed;
  }

  // 檢查角色和其他物體之間的碰撞
  if (checkCollision(player, rock)) {
    // 如果碰到 rock，角色和 rock 都可以推動它
    player.x += dirX * speed;
    player.y += dirY * speed;
    rock.x += dirX * speed;
    rock.y += dirY * speed;
  }
};
